!function ($) {

  "use strict"; // jshint ;_;


 /* DROPDOWN CLASS DEFINITION
  * ========================= */

  var toggle = '[data-toggle=dropdown]'//属性标记
    , Dropdown = function (element) {//构造器
        var $el = $(element).on('click.dropdown.data-api', this.toggle)
        $('html').on('click.dropdown.data-api', function () {
          $el.parent().removeClass('open')
        })
      }

  Dropdown.prototype = {//构造器的原型

    constructor: Dropdown

  , toggle: function (e) {
      var $this = $(this)
        , $parent
        , isActive

      if ($this.is('.disabled, :disabled')) return

      $parent = getParent($this)
//获得父节点
      isActive = $parent.hasClass('open')

      clearMenus()
//如果li标签上没有open类则加上open类
      if (!isActive) {
        $parent.toggleClass('open')
        $this.focus()
      }

      return false//阻止冒泡
    }

  , keydown: function (e) {
      var $this
        , $items
        , $active
        , $parent
        , isActive
        , index

      if (!/(38|40|27)/.test(e.keyCode)) return

      $this = $(this)

      e.preventDefault()
      e.stopPropagation()

      if ($this.is('.disabled, :disabled')) return

      $parent = getParent($this)

      isActive = $parent.hasClass('open')

      if (!isActive || (isActive && e.keyCode == 27)) return $this.click()

      $items = $('[role=menu] li:not(.divider) a', $parent)

      if (!$items.length) return

      index = $items.index($items.filter(':focus'))

      if (e.keyCode == 38 && index > 0) index--                                        // up
      if (e.keyCode == 40 && index < $items.length - 1) index++                        // down
      if (!~index) index = 0

      $items
        .eq(index)
        .focus()
    }

  }

  function clearMenus() {//自定义方法
    getParent($(toggle))
      .removeClass('open')//如果点击文档，则执行将li标签去除open类，其实这个open类也是个标记，可利用扩展
  }

  function getParent($this) {
    var selector = $this.attr('data-target')
      , $parent
//如果没有data-target属性，则使用a标签的href属性，根据正则渠道其#和#以后的字符串，放入jquery容器中变为jquery对象
    if (!selector) {
      selector = $this.attr('href')
      selector = selector && /#/.test(selector) && selector.replace(/.*(?=#[^\s]*$)/, '') //strip for ie7
    }

    $parent = $(selector)
    $parent.length || ($parent = $this.parent())

    return $parent
  }


  /* DROPDOWN PLUGIN DEFINITION
   * ========================== */

  $.fn.dropdown = function (option) {//jquery原型上的自定义的方法
    return this.each(function () {
      var $this = $(this)
        , data = $this.data('dropdown')
      if (!data) $this.data('dropdown', (data = new Dropdown(this)))
      if (typeof option == 'string') data[option].call($this)
    })
  }

  $.fn.dropdown.Constructor = Dropdown//重写方法的构造函数名


  /* APPLY TO STANDARD DROPDOWN ELEMENTS
   * =================================== */

  $(function () {//默认初始化执行
    $('.dropdown-toggle').dropdown();
    $().dropdown('toggle');
    $('html')
      .on('click.dropdown.data-api touchstart.dropdown.data-api', clearMenus)//初始化时，给html加入监听事件click，触发clearMenus方法
    $('body')
      .on('click.dropdown touchstart.dropdown.data-api', '.dropdown form', function (e) { e.stopPropagation() })
      .on('click.dropdown.data-api touchstart.dropdown.data-api'  , toggle, Dropdown.prototype.toggle)//给body加入监听事件让toggle对象触发Dropdown原型上的方法
      .on('keydown.dropdown.data-api touchstart.dropdown.data-api', toggle + ', [role=menu]' , Dropdown.prototype.keydown)
  })

}(window.jQuery);